import React, { useEffect, useState, useContext } from 'react';
import './css/ReviewMovie.css';

import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import { MoviesContext } from '../../context/MoviesContext';
import axios from 'axios';
import { useParams } from 'react-router-dom';

import StarIcon from '@material-ui/icons/Star';

const ReviewMovie = () => {
    const [movies, setMovies] = useState(null)
    const { id } = useParams();
    const useStyles = makeStyles((theme) => ({
        root: {
            flexGrow: 1,
        },
        paper: {
            padding: theme.spacing(2),
            textAlign: 'center',
            color: theme.palette.text.secondary,
        },
    }));
    useEffect(() => {
        if (movies === null) {
            axios.get(`https://backendexample.sanbersy.com/api/movies/${id}`)
                .then(res => {
                    setMovies(res.data)
                })
        }
    })
    console.log(movies)
    const classes = useStyles();
    return (
        <>
            <div id="contentreview">
                <div id="bagianatas">
                    <div id="kotakitem">
                        {movies !== null &&
                            <>
                                <div id="itemjudul">{movies.title}{movies.year}</div>
                                <div id="itemrating"><StarIcon /> {movies.rating}<sub>/10</sub></div>
                                <div id="itemkecil">{movies.duration} min | {movies.genre} | {movies.year}</div>
                            </>
                        }
                    </div>
                    <img />
                </div>

            </div>
        </>
    )
}
export default ReviewMovie;
