import React, { useState, useContext, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import { MoviesContext } from '../../context/MoviesContext';

import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Button from '@material-ui/core/Button';
const EditMovie = () => {
    const [movies, setMovies] = useContext(MoviesContext);
    const [input, setInput] = useState(
        {
            title: "",
            description: "",
            year: "",
            duration: "",
            genre: "",
            rating: "",
            review: "",
            image_url: ""
        }
    )
    let { id } = useParams();
    console.log(movies);

    useEffect(() => {
        if (movies.statusForm === "edit") {
            axios.get(`https://backendexample.sanbersy.com/api/movies/${id}`)
                .then(res => {
                    setInput(res.data.map(el => {
                        return {
                            id: el.id,
                            created_at: el.created_at,
                            updated_at: el.updated_at,
                            title: el.title,
                            description: el.description,
                            year: el.year,
                            duration: el.duration,
                            genre: el.genre,
                            rating: el.rating,
                            review: el.review,
                            image_url: el.image_url
                        }

                    }))
                })
        }
    }
    )
    console.log(input)
    console.log(movies);

    const handleChange = () => {

    }
    const handleSubmit = () => {

    }

    return (
        <>
            <div id="contentcreate">
                <h1><span>Add Movie</span></h1 >
                <form style={{ margin: "20px", padding: "15px" }} onSubmit={handleSubmit}>
                    <div>
                        <InputLabel style={{ float: "right", marginRight: "400px", color: "black", fontSize: "16px", fontWeight: 'bolder' }}>Year Release:</InputLabel>
                        <InputLabel style={{ marginLeft: "10px", color: "black", fontSize: "16px", fontWeight: 'bolder' }} >Movie Title:</InputLabel>
                        <TextField
                            style={{ width: "400px", marginTop: '10px' }}
                            type="text"
                            id="outlined-secondary"
                            label="Movie Title"
                            variant="outlined"
                            name="title"
                            onChange={handleChange}
                            value={input.title}

                        />

                        <TextField
                            style={{
                                marginLeft: "100px",
                                width: "400px", marginTop: '10px'
                            }}
                            type="number"
                            id="outlined-secondary"
                            label="Year"
                            variant="outlined"
                            name="year"
                            onChange={handleChange}
                            value={input.year}
                        />
                    </div>
                    <div style={{ marginTop: "50px" }}>
                        <InputLabel style={{ float: "right", marginRight: "430px", color: "black", fontSize: "16px", fontWeight: 'bolder' }}>Duration:</InputLabel>
                        <InputLabel style={{ marginLeft: "10px", color: "black", fontSize: "16px", fontWeight: 'bolder' }} >Description:</InputLabel>
                        <TextField
                            style={{ width: "400px", marginTop: '10px' }}
                            id="outlined-multiline-static"
                            label="Description"
                            multiline
                            rows={4}
                            variant="outlined"
                            name="description"
                            onChange={handleChange}
                            value={input.description}
                        />

                        <TextField
                            style={{ marginLeft: "100px", width: "400px", marginTop: '10px' }}
                            type="number"
                            id="outlined-secondary"
                            label="Duration"
                            variant="outlined"
                            name="duration"
                            onChange={handleChange}
                            value={input.duration}
                            min={0}
                            max={10}
                            inputProps={{ inputProps: { min: 0, max: 10 } }}
                        />
                    </div>
                    <div style={{ marginTop: "50px" }}>
                        <InputLabel style={{ float: "right", marginRight: "400px", color: "black", fontSize: "16px", fontWeight: 'bolder' }}>Image (URL):</InputLabel>
                        <InputLabel style={{ marginLeft: "10px", color: "black", fontSize: "16px", fontWeight: 'bolder' }} >Genre:</InputLabel>
                        <TextField
                            style={{ width: "400px", marginTop: '10px' }}
                            type="text"
                            id="outlined-secondary"
                            label="Genre"
                            variant="outlined"
                            name="genre"
                            onChange={handleChange}
                            value={input.genre}
                        />

                        <TextField
                            style={{ marginLeft: "100px", width: "400px", marginTop: '10px' }}
                            type="text"
                            id="outlined-secondary"
                            label="Movie Image (URL):"
                            variant="outlined"
                            name="image_url"
                            onChange={handleChange}
                            value={input.image_url}
                        />
                    </div>
                    <div style={{ marginTop: "50px" }}>
                        <InputLabel style={{ float: "right", marginRight: "450px", color: "black", fontSize: "16px", fontWeight: 'bolder' }}>Review:</InputLabel>
                        <InputLabel style={{ marginLeft: "10px", color: "black", fontSize: "16px", fontWeight: 'bolder' }} >Rating:</InputLabel>
                        <TextField
                            style={{ width: "400px", marginTop: '10px' }}
                            type="number"
                            id="outlined-secondary"
                            label="Rating"
                            variant="outlined"
                            name="rating"
                            onChange={handleChange}
                            value={input.rating}

                            inputProps={{ inputProps: { min: 0, max: 10 } }}
                        />
                        <TextField
                            style={{ marginLeft: "100px", width: "400px" }}
                            id="outlined-multiline-static"
                            label="Review"
                            multiline
                            rows={4}
                            variant="outlined"
                            name="review"
                            onChange={handleChange}
                            value={input.review}
                        />

                    </div>

                    <div style={{ marginTop: "50px" }}>
                        <Button type="Submit" variant="contained" color="secondary">
                            Submit
                    </Button>
                    &nbsp;
                    <Button variant="contained" color="warning" >
                            <Link to="movie/tablemovie" style={{ textDecoration: "none" }}>
                                Back
                      </Link>
                        </Button>
                    </div>
                </form >
            </div >
        </>
    )
}
export default EditMovie;
