import React from 'react';
import { useState } from 'react';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import Checkbox from '@material-ui/core/Checkbox';


import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import FormControlLabel from '@material-ui/core/FormControlLabel';
const CreateGame = () => {
    const [input, setInput] = useState({
        name: "",
        genre: "",
        singleplayer: "",
        multiplayer: "",
        platform: "",
        release: 0,
        image_url: ""
    });

    const handleChange = (event) => {
        let getName = event.target.name;

        switch (getName) {
            case "name": setInput({ ...input, name: event.target.value })
                break;
            case "genre": setInput({ ...input, genre: event.target.value })
                break;
            case "singleplayer": setInput({ ...input, genre: event.target.check })
                breakl
            case "multiplayer": if (event.target.value === true) {
                setInput({ ...input, multiplayer: "Yes" }
                )
            } else {
                setInput({ ...input, multiplayer: false })
            }
                break;
            case "platform": setInput({ ...input, platform: event.target.value })
                break;
            case "release": setInput({ ...input, release: event.target.value })
                break;
            case "image_url": setInput({ ...input, image_url: event.target.value })
                break;
            default: { break; }
        }
        const handleChecked = (event) => {
            let getName = event.target.name
            let getValue = event.target.value

            if (getName === "singleplayer" && getValue === true) {
                setInput({ ...input, singleplayer: 1 })
            } else if (getName === "singleplayer" && getValue === "") {
                setInput({ ...input, singleplayer: 2 })
            }
        }
    }
    console.log(input)

    return (
        <>
            <div id="contentcreate">
                <h1>cek</h1>
                <h1><span>Add Movie</span></h1 >
                <form style={{ margin: "20px", padding: "15px" }}>
                    <div>
                        <InputLabel style={{ float: "right", marginRight: "450px", color: "black", fontSize: "16px", fontWeight: 'bolder' }}>Genre:</InputLabel>
                        <InputLabel style={{ marginLeft: "10px", color: "black", fontSize: "16px", fontWeight: 'bolder' }} >Game Title:</InputLabel>
                        <TextField
                            style={{ width: "400px", marginTop: '10px' }}
                            type="text"
                            id="outlined-secondary"
                            label="Game Title"
                            variant="outlined"
                            name="name"
                            onChange={handleChange}
                        // value={input.title}

                        />

                        <TextField
                            style={{
                                marginLeft: "100px",
                                width: "400px", marginTop: '10px'
                            }}
                            type="text"
                            id="outlined-secondary"
                            label="Genre"
                            variant="outlined"
                            name="genre"
                            onChange={handleChange}
                        // value={input.year}
                        />
                    </div>
                    <div style={{ marginTop: "50px" }}>
                        <InputLabel style={{ float: "right", marginRight: "430px", color: "black", fontSize: "16px", fontWeight: 'bolder' }}>Release:</InputLabel>
                        <InputLabel style={{ marginLeft: "10px", color: "black", fontSize: "16px", fontWeight: 'bolder' }} >Platform:</InputLabel>
                        <TextField
                            style={{ width: "400px", marginTop: '10px' }}
                            type="text"
                            id="outlined-secondary"
                            label="Platform"
                            variant="outlined"
                            name="platform"
                            onChange={handleChange}
                        // value={input.description}

                        />

                        <TextField
                            style={{ marginLeft: "100px", width: "400px", marginTop: '10px' }}
                            type="date"
                            id="outlined-secondary"
                            variant="outlined"
                            name="release"
                            onChange={handleChange}
                            // value={input.duration}
                            inputProps={{ min: "1", step: "1" }}
                        />
                    </div>
                    <div style={{ marginTop: "50px" }}>
                        <InputLabel style={{ float: "right", marginRight: "430px", color: "black", fontSize: "16px", fontWeight: 'bolder' }}>Duration:</InputLabel>
                        <InputLabel style={{ marginLeft: "10px", color: "black", fontSize: "16px", fontWeight: 'bolder' }} >Image URL:</InputLabel>
                        <TextField
                            style={{ width: "400px", marginTop: '10px' }}
                            type="text"
                            id="outlined-secondary"
                            label="image URL"
                            variant="outlined"
                            name="image_url"
                            onChange={handleChange}
                            value={input.image_url}
                        />
                        <FormControlLabel
                            control={
                                <Checkbox
                                    checked={input.singleplayer}
                                    onChange={handleChange}
                                    name="singleplayer"
                                    color="primary"
                                />
                            }
                            label="SinglePLayer"
                        />
                    </div>
                </form>
            </div>
        </>
    )
}
export default CreateGame;
