import React, { useState, useEffect } from 'react';

import './css/TableMovie.css';

import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Axios from 'axios';

// import MaterialTable from 'material-table';


const StyledTableCell = withStyles((theme) => ({
    head: {
        backgroundColor: theme.palette.common.black,
        color: theme.palette.common.white,
        textWeight: "bold"
    },
    body: {
        fontSize: 15,
    },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
    root: {
        '&:nth-of-type(odd)': {
            backgroundColor: theme.palette.action.hover,
        },
    },
}))(TableRow);


const useStyles = makeStyles({
    table: {
        minWidth: 700,
    },
});

const TableMovie = () => {
    const classes = useStyles();

    const [movies, setMovies] = useState(null);

    useEffect(() => {
        if (movies === null) {

            Axios.get(`https://backendexample.sanbersy.com/api/movies`)
                .then(res => {

                    setMovies(res.data.map(el => {
                        return {
                            id: el.id,
                            created_at: el.created_at,
                            updated_at: el.updated_at,
                            title: el.title,
                            description: el.description,
                            year: el.year,
                            duration: el.duration,
                            genre: el.genre,
                            rating: el.rating,
                            review: el.review,
                            image_url: el.image_url
                        }

                    }))
                })
        }
    })

    return (
        <>

            <div id="content">
                <br />
                <h1><span>Movies Lists</span></h1>
                <h4><span style={{ color: "gold", fontWeight: "700", marginTop: "10px", backgroundColor: "black", padding: "5px", borderRadius: "4px" }}>Daftar berbagai macam film dalam satu tabel</span></h4>
                <TableContainer style={{ width: "100%" }} component={Paper} id="table">
                    <Table style={{ width: "100%" }} className={classes.table} aria-label="simple table">
                        <TableHead style={{ backgroundColor: "gold" }}>
                            <TableRow >
                                <TableCell style={{ color: "white", fontWeight: "bolder" }}>No</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Title</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Description</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Year</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Duration</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Genre</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Rating</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Review</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Image URL</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {movies != null && movies.map((item, index) => {
                                return (
                                    <>
                                        <TableRow key={index}>
                                            <TableCell component="th" scope="row">{index + 1}</TableCell>
                                            <TableCell align="right">{item.title}</TableCell>
                                            <TableCell align="right">{item.description}</TableCell>
                                            <TableCell align="right">{item.year}</TableCell>
                                            <TableCell align="right">{item.duration}</TableCell>
                                            <TableCell align="right">{item.genre}</TableCell>
                                            <TableCell align="right">{item.rating}</TableCell>
                                            <TableCell align="right">{item.review}</TableCell>
                                            <TableCell align="right">{item.image_url}</TableCell>
                                        </TableRow>
                                    </>
                                )
                            })
                            }

                        </TableBody>
                    </Table>
                </TableContainer>
            </div>
        </>
    );
}
export default TableMovie;
