import React, { useContext, useState, useEffect } from 'react';
import './css/CreateMovie.css'

import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import { makeStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';

import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';

import { MoviesContext } from '../../context/MoviesContext';
import axios from 'axios';






const CreateMovie = () => {
    const [movies, setMovies] = useContext(MoviesContext);
    console.log(movies);
    const [input, setInput] = useState(
        {
            title: "",
            description: "",
            year: 0,
            duration: 0,
            genre: "",
            rating: 0,
            review: "",
            image_url: ""
        }
    )

    useEffect(() => {
        if (movies.lists === null) {
            axios.get(`https://backendexample.sanbersy.com/api/movies`)
                .then(res => {
                    setMovies(res.data.map(el => {
                        return {
                            id: el.id,
                            created_at: el.created_at,
                            updated_at: el.updated_at,
                            title: el.title,
                            description: el.description,
                            year: el.year,
                            duration: el.duration,
                            genre: el.genre,
                            rating: el.rating,
                            review: el.review,
                            image_url: el.image_url
                        }
                    }))
                })
        }
    })

    const handleChange = (event) => {
        let getName = event.target.name;

        switch (getName) {
            case "title": setInput({ ...input, title: event.target.value })

                break;
            case "description": setInput({ ...input, description: event.target.value })
                break;

            case "duration":
                setInput({ ...input, duration: event.target.value })
                break;
            case "genre": setInput({ ...input, genre: event.target.value })
                break;
            case "rating": setInput({ ...input, rating: event.target.value })
                break;
            case "review": setInput({ ...input, review: event.target.value })
                break;
            case "image_url": setInput({ ...input, image_url: event.target.value })
                break;
            default: { break; }
        }
    }

    const handleSubmit = (event) => {
        let title = input.title;
        let description = input.description;
        let duration = input.duration;
        let genre = input.genre;
        let rating = input.rating;
        let review = input.review;
        let image_url = input.image_url;

        if (movies.statusForm === "create") {
            axios.post(`https://backendexample.sanbersy.com/api/movies`, {
                created_at: date('Y-m-d H:i:s', time()),
                title: input.title, description: input.description,
                duration: input.duration, genre: input.genre, rating: input.rating,
                review: input.review, image_url: input.image_url
            })
                .then(res => {
                    setMovies([...movies, {
                        id: res.data.id,
                        ...input
                    }])
                })
        }
    }

    return (
        <div id="content">
            <h1><span>Add Movie</span></h1>
            <FormControl style={{ margin: "20px", padding: "15px" }} onSubmit={handleSubmit}>
                <div>
                    <TextField
                        style={{ width: "400px" }}
                        type="text"
                        id="outlined-secondary"
                        label="Movie Title"
                        variant="outlined"
                        name="title"
                        onChange={handleChange}

                    />
                    <TextField
                        style={{
                            marginLeft: "100px",
                            width: "400px"
                        }}
                        type="number"
                        id="outlined-secondary"
                        label="Year"
                        variant="outlined"
                        name="year"
                        onChange={handleChange}
                    />
                </div>
                <div style={{ marginTop: "20px" }}>
                    <TextField
                        style={{ width: "400px" }}
                        id="outlined-multiline-static"
                        label="Description"
                        multiline
                        rows={4}
                        variant="outlined"
                        name="description"
                        onChange={handleChange}
                    />

                    <TextField
                        style={{ marginLeft: "100px", width: "400px" }}
                        type="number"
                        id="outlined-secondary"
                        label="Duration"
                        variant="outlined"
                        name="duration"
                        onChange={handleChange}
                    />
                </div>
                <div style={{ marginTop: "20px" }}>
                    <TextField
                        style={{ width: "400px" }}
                        type="text"
                        id="outlined-secondary"
                        label="Genre"
                        variant="outlined"
                        name="genre"
                        onChange={handleChange}
                    />
                </div>
                <div style={{ marginTop: "20px" }}>
                    <Typography id="discrete-slider" gutterBottom>
                        Rating
                     </Typography>
                    <Slider
                        defaultValue="0"
                        getAriaValueText=""
                        aria-labelledby="discrete-slider"
                        valueLabelDisplay="auto"
                        step={0.5}
                        marks
                        min={0}
                        max={10}
                        style={{ width: "400px" }}
                        name="rating"
                        onChange={handleChange}
                    />

                    <TextField
                        style={{ marginLeft: "100px", width: "400px" }}
                        id="outlined-multiline-static"
                        label="Review"
                        multiline
                        rows={4}
                        variant="outlined"
                        name="review"
                        onChange={handleChange}
                    />

                </div>
                <div style={{ marginTop: "20px" }}>
                    <TextField
                        style={{ width: "400px" }}
                        type="text"
                        id="outlined-secondary"
                        label="Movie Image (URL):"
                        variant="outlined"
                        name="image_url"
                        onChange={handleChange}
                    />
                </div>
                <div style={{ marginTop: "20px" }}>
                    <Button variant="contained" color="secondary">
                        Submit
                    </Button>
                </div>
            </FormControl >
        </div >
    )
}
export default CreateMovie;
