import React, { useContext, useState } from 'react';
import './css/CreateMovie.css'

import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import { makeStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';

import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Slider from '@material-ui/core/Slider';

import { MoviesContext } from '../../context/MoviesContext';



const classes = useStyles();


const CreateMovie = () => {
    const [movies, setMovies] = useContext(MoviesContext);
    const [input, setInput] = useState(
        {
            title: "",
            description: "",
            year: 0,
            duration: 0,
            genre: "",
            rating: 0,
            review: "",
            image_url: ""
        }
    )


    const handlerChange = (event) => {
        let getName = event.target.name;

        switch (getName) {
            case "title": setInput({ ...input, title: event.target.value })

                break;
            case "description": setInput({ ...input, description: event.target.value })
                break;

            case "duration":
                setInput({ ...input, duration: event.target.value })
                break;
            case "genre": setInput({ ...input, genre: event.target.value })
                break;
            case "rating": setInput({ ...input, rating: event.target.value })
                break;
            case "review": setInput({ ...input, review: event.target.value })
                break;
            case "image_url": setInput({ ...input, image_url: event.target.value })
                break;
            default: { break; }
        }
    }

    return (
        <div id="content">
            <h1><span>Add Movie</span></h1>
            <FormControl style={{ margin: "20px", padding: "15px" }}>
                <div>
                    <TextField
                        style={{ width: "400px" }}
                        type="text"
                        id="outlined-secondary"
                        label="Movie Title"
                        variant="outlined"
                        name="title"
                        value={handlerChange}

                    />
                    <TextField
                        style={{
                            marginLeft: "100px",
                            width: "400px"
                        }}
                        type="number"
                        id="outlined-secondary"
                        label="Year"
                        variant="outlined"
                        name="year"
                        value={handlerChange}
                    />
                </div>
                <div style={{ marginTop: "20px" }}>
                    <TextField
                        style={{ width: "400px" }}
                        id="outlined-multiline-static"
                        label="Description"
                        multiline
                        rows={4}
                        variant="outlined"
                        name="description"
                        value={handlerChange}
                    />

                    <TextField
                        style={{ marginLeft: "100px", width: "400px" }}
                        type="number"
                        id="outlined-secondary"
                        label="Duration"
                        variant="outlined"
                        name="duration"
                        value={handlerChange}
                    />
                </div>
                <div style={{ marginTop: "20px" }}>
                    <TextField
                        style={{ width: "400px" }}
                        type="number"
                        id="outlined-secondary"
                        label="Genre"
                        variant="outlined"
                        name="genre"
                        value={handlerChange}
                    />
                </div>
                <div style={{ marginTop: "20px" }}>
                    <Typography id="discrete-slider" gutterBottom>
                        Rating
                     </Typography>
                    <Slider
                        defaultValue="0"
                        getAriaValueText=""
                        aria-labelledby="discrete-slider"
                        valueLabelDisplay="auto"
                        step={0.5}
                        marks
                        min={0}
                        max={10}
                        style={{ width: "400px" }}
                        name="rating"
                        value={handlerChange}
                    />

                    <TextField
                        style={{ marginLeft: "100px", width: "400px" }}
                        id="outlined-multiline-static"
                        label="Review"
                        multiline
                        rows={4}
                        variant="outlined"
                        name="review"
                        value={handlerChange}
                    />

                </div>
                <div style={{ marginTop: "20px" }}>
                    <TextField
                        style={{ width: "400px" }}
                        type="text"
                        id="outlined-secondary"
                        label="Movie Image (URL):"
                        variant="outlined"
                        name="image_url"
                        value={handlerChange}
                    />
                </div>
                <div style={{ marginTop: "20px" }}>
                    <Button variant="contained" color="secondary">
                        Submit
                    </Button>
                </div>
            </FormControl >
        </div >
    )
}
export default CreateMovie;
