import React, { useState, useEffect } from 'react';
import { MoviesContext } from '../../context/MoviesContext'
import { Redirect, Link, useHistory } from 'react-router-dom';
import './css/TableMovie.css';

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

import EditIcon from '@material-ui/icons/Edit';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';

import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';

import { Link as Linkto } from 'react';


import axios from 'axios';

// import MaterialTable from 'material-table';


const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

const TableMovie = () => {
    const classes = useStyles();
    const history = useHistory();
    const [movies, setMovies] = useState(null);
    const [click, setClick] = useState(true)
    useEffect(() => {
        if (movies === null) {
            axios.get(`https://backendexample.sanbersy.com/api/movies`)
                .then(res => {

                    setMovies(res.data.map(el => {
                        return {
                            id: el.id,
                            created_at: el.created_at,
                            updated_at: el.updated_at,
                            title: el.title,
                            description: el.description,
                            year: el.year,
                            duration: el.duration,
                            genre: el.genre,
                            rating: el.rating,
                            review: el.review,
                            image_url: el.image_url
                        }

                    }))
                })
        }
    })
    console.log(movies)

    const Actions = ({ itemsid }) => {

        const handleDelete = () => {
            console.log(itemsid)
            let newMovies = movies.filter(el => el.id !== itemsid);

            axios.delete(`https://backendexample.sanbersy.com/api/movies/${itemsid}`)
                .then(res => {
                    console.log(res);
                })
            setMovies([...newMovies])


        }

        const handleEdit = () => {
            history.push(`/movie/tablemovie/edit/${itemsid}`)

        }

        return (
            <>
                <Button
                    variant="contained"
                    color="primary"
                    className={classes.button}
                    onClick={handleDelete}
                    style={{ backgroundColor: "gold", color: "black", fontWeight: 'bolder' }}> <DeleteForeverIcon />
                </Button>
                                                &nbsp;
                <Button
                    variant="contained"
                    color="primary"
                    className={classes.button}
                    onClick={handleEdit}
                    style={{ backgroundColor: "gold", color: "black", fontWeight: 'bolder' }}> <EditIcon />
                </Button>
                {/* <Link to={`/movie/tablemovie/edit/${itemsid}`}>Edit</Link> */}
            </>
        )
    }

    const sortbyYear = (event) => {
        event.preventDefault();

        if (click === true) {
            var year = movies.sort((x1, x2) => parseInt(x1.year) - parseInt(x2.year))
            console.log(click);
            setClick(false);

        } else if (click === false) {
            var year = movies.sort((x1, x2) => parseInt(x2.year) - parseInt(x1.year))
            console.log(click);
            setClick(true);
        }
        console.log(click)
        setMovies([...year]);
    }
    const sortbyGenre = (event) => {

    }
    const sortbyRating = (event) => {

    }
    return (
        <>

            <div id="content">
                <h1><span>Movies Lists</span></h1>
                {/* <h4><span style={{ color: "white", fontWeight: "700", marginTop: "5px", backgroundColor: "black", padding: "5px", borderRadius: "4px" }}>Daftar berbagai macam film dalam satu tabel</span></h4> */}


                {/* This Button uses a Font Icon, see the installation instructions in the Icon component docs. */}


                <Button
                    href="/movie/tablemovie/create"
                    variant="contained"
                    color="primary"
                    className={classes.button}
                    style={{ marginTop: "40px", marginLeft: "65%", fontSize: "19px", width: "150px", backgroundColor: "gold", color: "black", fontWeight: 'bolder' }}
                > Add Movie
                 </Button>
                <br />

                {/* ...................................table.............................. */}
                <TableContainer style={{ width: "90%", backgroundColor: "white" }} id="table">
                    <Table className={classes.table} aria-label="simple table">
                        <TableHead >
                            <TableRow >
                                <TableCell style={{ color: "black", fontSize: "17px" }} align="right">Actions</TableCell>
                                <TableCell style={{ color: "black", fontSize: "17px" }}>No</TableCell>
                                <TableCell style={{ color: "black", fontSize: "17px" }} align="right">Title</TableCell>
                                <TableCell style={{ color: "black", fontSize: "17px" }} align="right">Description</TableCell>
                                <TableCell style={{ color: "black", fontSize: "17px", width: "150px" }} align="left">Year
                                    {click === true && <ArrowUpwardIcon style={{ display: "inline" }} onClick={sortbyYear} />}
                                    {click === false && <ArrowDownwardIcon style={{ display: "inline" }} onClick={sortbyYear} />}

                                </TableCell>


                                <TableCell style={{ color: "black", fontSize: "17px" }} align="right">Duration</TableCell>
                                <TableCell style={{ color: "black", fontSize: "17px" }} align="center">Genre
                                <br />
                                    <Button
                                        variant="contained"
                                        color="primary"
                                        className={classes.button}
                                        onClick={sortbyGenre}
                                        style={{ width: "1px", height: "10px", backgroundColor: "black", color: "white", fontWeight: 'bolder' }}> <ArrowDownwardIcon />
                                    </Button></TableCell>
                                <TableCell style={{ color: "black", fontSize: "17px" }} align="center">Rating<br />
                                    <Button
                                        variant="contained"
                                        color="primary"
                                        className={classes.button}
                                        onClick={sortbyRating}
                                        style={{ width: "1px", height: "10px", backgroundColor: "black", color: "white", fontWeight: 'bolder' }}>
                                        {/* {(click === true) ? <ArrowDownwardIcon /> : <ArrowUpwardIcon />} */}


                                    </Button></TableCell>
                                <TableCell style={{ color: "black", fontSize: "17px" }} align="right">Review</TableCell>
                                <TableCell style={{ color: "black", fontSize: "17px" }} align="center">Image URL</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {movies != null && movies.map((item, index) => {
                                return (
                                    <>
                                        <TableRow key={index}>
                                            <TableCell align="right">
                                                <Actions itemsid={item.id} />
                                            </TableCell>
                                            <TableCell component="th" scope="row">{index + 1}</TableCell>
                                            <TableCell align="center">{item.title}</TableCell>
                                            <TableCell align="right">{item.description}</TableCell>
                                            <TableCell align="right">{item.year}</TableCell>
                                            <TableCell align="right">{item.duration}</TableCell>
                                            <TableCell align="right">{item.genre}</TableCell>
                                            <TableCell align="right">{item.rating}</TableCell>
                                            <TableCell align="right">{item.review}</TableCell>
                                            <TableCell align="right">{item.image_url}</TableCell>
                                        </TableRow>
                                    </>
                                )
                            })
                            }

                        </TableBody>
                    </Table>
                </TableContainer>
                {/* ...............akhir table................... */}



            </div>
        </>
    );
}
export default TableMovie;
