import React, { useState, useEffect } from 'react';

import './css/TableMovie.css';

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

import EditIcon from '@material-ui/icons/Edit';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';

import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';

import { Link as Linkto } from 'react';


import axios from 'axios';

// import MaterialTable from 'material-table';


const useStyles = makeStyles({
    table: {
        minWidth: 650,
    },
});

const TableMovie = () => {
    const classes = useStyles();

    const [movies, setMovies] = useState(null);

    useEffect(() => {
        if (movies === null) {

            axios.get(`https://backendexample.sanbersy.com/api/movies`)
                .then(res => {

                    setMovies(res.data.map(el => {
                        return {
                            id: el.id,
                            created_at: el.created_at,
                            updated_at: el.updated_at,
                            title: el.title,
                            description: el.description,
                            year: el.year,
                            duration: el.duration,
                            genre: el.genre,
                            rating: el.rating,
                            review: el.review,
                            image_url: el.image_url
                        }

                    }))
                })
        }
    })
    console.log(movies)

    const Actions = ({ itemsid }) => {

        const handleDelete = () => {
            console.log(itemsid)
            let newMovies = movies.filter(el => el.id !== itemsid);

            axios.delete(`https://backendexample.sanbersy.com/api/movies/${itemsid}`)
                .then(res => {
                    console.log(res);
                })
            setMovies([...newMovies])

            const handleEdit = () => {
                setMovies({ ...movies, statusForm: "edit", selectedId: itemsid })
            }
        }
        return (
            <>
                <Button
                    variant="contained"
                    color="primary"
                    className={classes.button}
                    style={{ backgroundColor: "gold", color: "black", fontWeight: 'bolder' }}> <EditIcon />
                    {/* onClick={handleEdit} */}
                </Button>
                                                &nbsp;
                <Button
                    variant="contained"
                    color="primary"
                    className={classes.button}
                    onClick={handleDelete}
                    style={{ backgroundColor: "black", color: "gold", fontWeight: 'bolder' }}><DeleteForeverIcon />
                </Button>
            </>
        )
    }

    return (
        <>

            <div id="content">
                <h1><span>Movies Lists</span></h1>
                {/* <h4><span style={{ color: "white", fontWeight: "700", marginTop: "5px", backgroundColor: "black", padding: "5px", borderRadius: "4px" }}>Daftar berbagai macam film dalam satu tabel</span></h4> */}


                {/* This Button uses a Font Icon, see the installation instructions in the Icon component docs. */}


                <Button
                    href="movie/tablemovie/create"
                    variant="contained"
                    color="primary"
                    className={classes.button}
                    style={{ marginTop: "40px", marginLeft: "65%", fontSize: "19px", width: "150px", backgroundColor: "gold", color: "black", fontWeight: 'bolder' }}
                > Add Movie
                 </Button>
                <br />

                {/* ...................................table.............................. */}
                <TableContainer style={{ width: "95%" }} component={Paper} id="table">
                    <Table className={classes.table} aria-label="simple table">
                        <TableHead style={{ backgroundColor: "black" }}>
                            <TableRow >
                                <TableCell style={{ color: "white", fontWeight: "bolder" }}>No</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Title</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Description</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Year</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Duration</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Genre</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Rating</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Review</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="center">Image URL</TableCell>
                                <TableCell style={{ color: "white", fontWeight: "bolder" }} align="right">Actions</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {movies != null && movies.map((item, index) => {
                                return (
                                    <>
                                        <TableRow key={index}>
                                            <TableCell component="th" scope="row">{index + 1}</TableCell>
                                            <TableCell align="right">{item.title}</TableCell>
                                            <TableCell align="right">{item.description}</TableCell>
                                            <TableCell align="right">{item.year}</TableCell>
                                            <TableCell align="right">{item.duration}</TableCell>
                                            <TableCell align="right">{item.genre}</TableCell>
                                            <TableCell align="right">{item.rating}</TableCell>
                                            <TableCell align="right">{item.review}</TableCell>
                                            <TableCell align="right">{item.image_url}</TableCell>
                                            <TableCell align="right">
                                                <Actions itemsid={item.id} />
                                            </TableCell>
                                        </TableRow>
                                    </>
                                )
                            })
                            }

                        </TableBody>
                    </Table>
                </TableContainer>
            </div>
        </>
    );
}
export default TableMovie;
